mapper.select:
select count(id)
from t_front_plan
where arrangee_id=#{userId} and DATE_FORMAT(plan_date,'%Y-%m-%d') = #{today}

SELECT plan.id, plan.plan_date, plan.status, plan.shop_id, plan.sub_plan_id, shopinfo.shop_name FROM t_front_plan plan INNER JOIN t_shop_info shopinfo ON plan.shop_id = shopinfo.id AND shopinfo.is_del = FALSE AND shopinfo.is_use = TRUE WHERE plan.shop_id in ( SELECT shop.id from t_shop_info shop INNER JOIN sys_user_shop userShop ON userShop.shop_id = shop.id AND userShop.user_id IN
#{userid}
WHERE shop.area_id = #{areaId} ) AND plan.arrange_year = #{year} AND plan.arrange_month = #{month} AND plan.sub_plan_id = #{subPlanId} AND plan.is_del = FALSE

SELECT plan.shop_id AS shopId, COUNT(planForm.form_main_id) AS formCount, planForm.form_main_id AS formId FROM t_front_plan plan INNER JOIN t_front_plan_form planForm ON planForm.front_plan_id = plan.id AND planForm.report_status=1 AND planForm.is_del = FALSE INNER JOIN sys_user_shop userShop ON userShop.shop_id = plan.shop_id AND userShop.user_id IN
#{userid}
WHERE planForm.form_main_id IN
#{item}
AND plan.sub_plan_id = #{planId} AND plan.status=4 AND plan.is_del = FALSE GROUP BY plan.shop_id, planForm.form_main_id

mapper.update:
UPDATE t_front_plan
SET status = #{status}
WHERE id = #{arrangeId}
