mapper.select:
SELECT t1.*,t3.`name` AS taskTitle FROM t_loop_work t1 LEFT JOIN t_task t3 ON t1.task_id = t3.id WHERE t1.type != 4
AND t1.audit_user_id=#{auditUserId}
AND t1.excute_user_id=#{excuteUserId}
AND t1.create_user_id=#{createUserId}
AND ((year(t1.execute_begin_date)=#{yearStr} and month(t1.execute_begin_date) = #{monthStr}) OR (year(t1.execute_end_date)=#{yearStr} and month(t1.execute_end_date) = #{monthStr}) )
ORDER BY t1.audit_time DESC
ORDER BY t1.execute_deadline DESC
ORDER BY t1.create_time DESC

select * from t_loop_work where work_id = #{workId} and sub_work_id = #{subWorkId} and type=#{workType}
order by modify_time desc
limit 1

mapper.update:
        UPDATE t_loop_work
        
            
                work_status = #{workStatus},
            
            
                audit_deadline = #{auditDeadline},
            
            
                modify_time = #{modifyTime}
            
        
        WHERE work_id = #{workId} and sub_work_id = #{subWorkId} and type=#{type}

UPDATE t_loop_work

    
        audit_user_id = #{auditUserId},
    
    
        audit_user_name = #{auditUserName},
    
    
        work_result = #{workResult}
    
    
        modify_time = #{modifyTime}
    

WHERE work_id = #{workId} and sub_work_id = #{subWorkId} and type=#{type}

mapper.select:
select b.task_item_id as taskItemID, c.name as taskItemTitle, c.feedback_img_example_id as itemImgUrls, case when c.feedback_need_text = 0 then 1 else 2 end as feedbackText, case when c.feedback_img_type = 0 then 1 when c.feedback_img_type = 1 and c.feedback_img_from_album = 1 then 2 else 3 end as feedbackImg, c.feedback_num_name as digitalItemName, c.feedback_num_type as digitalItemType, b.id as taskSubID, d.head_img_url as taskSubHeadUrl, a.modify_time as taskSubTime, b.work_txt as workText, b.num_option_value as digitalItemValue, b.img_ids as imageUrls, b.audit_result as reviewResult, b.id as reviewID, b.audit_content as reviewOpinion, b.modify_time as reviewTime, b.audit_user_id as auditorID, e.real_name as auditorName, e.head_img_url as auditorHeadUrl, b.audit_img_ids as imgUrls
from t_operate_data_for_work b left join t_loop_work a on a.id = b.loop_work_id left join t_operate_option c on c.id = b.task_item_id left join sys_user d on a.excute_user_id = d.id left join sys_user e on b.audit_user_id = e.id
where a.id=#{loopWorkId}
