mapper.insert:
insert into t_opportunity (id, type, name, is_del, is_use, description, front_can_create, create_type, create_time, modify_time, create_user_id, modify_user_id, org_id)
values (#{id}, #{type}, #{name}, #{isDel}, #{isUse}, #{description}, #{frontCanCreate}, #{createType}, #{createTime}, #{modifyTime}, #{createUserId}, #{modifyUserId}, #{orgId})
on duplicate key update type=#{type}, name=#{name}, is_del=#{isDel}, is_use=#{isUse}, description=#{description}, front_can_create=#{frontCanCreate}, create_type=#{createType}, modify_time=#{modifyTime}, modify_user_id=#{modifyUserId}, org_id=#{orgId}

