mapper.select:
SELECT shop.id, shop.shop_name, shop.short_name, shop.shop_desc, shop.brand_id, shop.type, ( SELECT brand.brand_name FROM t_brand_info brand WHERE brand.id = shop.brand_id ) AS brandName FROM t_shop_info shop INNER JOIN sys_user_shop userShop ON userShop.shop_id = shop.id AND userShop.user_id IN
#{userid}
WHERE shop.area_id = #{areaId} AND shop.is_del = FALSE AND shop.is_use = TRUE

SELECT shopInfo.id, shopInfo.shop_name FROM t_shop_info shopInfo INNER JOIN sys_user_shop userShop ON userShop.shop_id = shopInfo.id AND userShop.user_id IN
#{userid}
AND shopInfo.is_del = FALSE AND shopInfo.is_use = TRUE

mapper.update:
UPDATE t_shop_info
SET shop_manager_id = NULL
WHERE shop_manager_id = #{userId}
